/**
 * Arguments for the `promptRequest` event.
 */
export interface InlineAIPromptRequestEvent {
    /**
     * The user prompt input.
     */
    prompt: string;
    /**
     * If `true`, the event is triggered by the built-in **Retry** action button.
     */
    isRetry?: boolean;
}
