/**
 * Represents a data row with an associated data item.
 *
 * Use this interface to access the data item and index for a row.
 */
export interface RowArgs {
    /**
     * The row data item.
     */
    dataItem: any;
    /**
     * The data row index.
     */
    index: number;
}
