/**
 * Specifies the animation type for the Loader component.
 *
 * The possible values are:
 * - `pulsing` (default)
 * - `infinite-spinner`
 * - `converging-spinner`
 *
 */
export type LoaderType = 'pulsing' | 'infinite-spinner' | 'converging-spinner';
