/**
 * Sets the spacing between layout items horizontally and vertically.
 */
export interface TileLayoutGap {
    /**
     * Sets the vertical spacing in pixels.
     */
    rows?: number;
    /**
     * Sets the horizontal spacing in pixels.
     */
    columns?: number;
}
