/**
 * Represents the settings of the animation that indicates the progress status of the ProgressBar.
 *
 */
export interface ProgressBarAnimation {
    /**
     * Sets the duration of the animation in milliseconds.
     *
     * @default 400
     */
    duration: number;
}
